declare function toBigInt(_value: BigNumberish | Uint8Array): bigint;
declare function toNumber(_value: BigNumberish | Uint8Array): number;
declare function toHex(_value: BigNumberish, width?: number): string;
declare function toArray(_value: BigNumberish): Uint8Array;

export { BigNumberish, toArray, toBigInt, toHex, toNumber };
